    private void itemPriceFieldFormatter() {
        // Set up the Spinner with the desired constraints
        itemPriceField.setModel(new javax.swing.SpinnerNumberModel(0.0, 0.0, null, 0.01));

        // Configure the display format to show 2 decimal points
        JSpinner.NumberEditor editor = new JSpinner.NumberEditor(itemPriceField, "0.00");
        itemPriceField.setEditor(editor);
    }

